mapper.select:
select * from tproduct where 1=1

    and barCode=#{barCode}


    and productName like CONCAT('%',#{productName},'%')


    and supply = #{supply}


    and price = #{price}

mapper.delete:
delete from tproduct where 1=1

    and barCode=#{barCode}


    and productName=#{productName}


    and supply = #{supply}


    and price = #{price}
