mapper.select:
/*采取动态SQL语句*/
        select * from tsaledetail where 1=1
                             /*通过改变默认构造函数来简化if*/
           and saletime like CONCAT('%',#{todayAnotherStr},'%')
        
        
            and barCode=#{barCode}
        
        
            and flowNum=#{flowNum}
        
        
            and price=#{price}
        
        
            and count=#{count}
        
        
            and operator=#{operator}

mapper.update:
update tsaledetail

    
        flowNum=#{flowNum}
    
    
        barCode=#{barCode}
    
    
        productName=#{productName}
    
    
        price=#{price}
    
    
        count=#{count}
    
    
        saletime=#{saletime}
    
    
        operator=#{operator}
    

where flowNum=#{flowNum}

mapper.delete:
delete from tsaledetail where 1=1

    and flowNum=#{flowNum}


    and barCode=#{barCode}


    and productName=#{productName}


    and price=#{price}


    and count=#{count}


    and saletime=#{saletime}


    and operator=#{operator}
